mapper.select:
SELECT * FROM (SELECT
(RANK()OVER(ORDER BY TO_NUMBER(T1.LAWID))) RNUM,
T1.LAWID, T1.LAWNO, T1.NOWHISTORYCODE, T1.LAWNAME, T1.CREATEDATE, T1.CREATENO, T1.ISMODIFY, T1.CAREDEPT, T1.LAWCLASSNAME, T1.ACTDATE, T1.ISSELF, T1.DETAILLINK,
T2.CATENO, T2.ISSHOW, T2.MODIFYTEXT, T2.MODIFYREASON FROM T_LAWLIST T1 LEFT OUTER JOIN T_LAWBASEINFO T2 ON T1.LAWID = T2.LAWID WHERE 1=1
AND LAWCLASSNAME = #{searchLawClassName}
AND LAWNAME LIKE '%' || #{searchKeyword} || '%'
AND ACTDATE BETWEEN #{startDate} AND #{endDate}
AND ACTDATE < #{endDate}
AND ACTDATE > #{startDate}
) WHERE RNUM BETWEEN #{firstIndex}+1 AND #{lastIndex}

SELECT COUNT(*) AS TOTALCOUNT FROM T_LAWLIST WHERE 1=1
AND LAWCLASSNAME = #{searchLawClassName}
AND LAWNAME LIKE '%' || #{searchKeyword} || '%'

 
    	SELECT	COUNT(*) AS TOTALCOUNT
		FROM 	T_LAWSUBTEXT
        WHERE 	LAWID = #{searchKeyword}

 
    	SELECT	COUNT(*) AS TOTALCOUNT
		FROM 	T_LAWARTICLE
        WHERE 	LAWID = #{searchKeyword}

 
    	SELECT	LAWID, ARTICLEKEY, MAINTEXT
		FROM 	T_LAWARTICLE
        WHERE 	LAWID = #{lawId}

mapper.update:
 
    	UPDATE	T_LAWBASEINFO
    	SET		CATENO = #{cateNo, jdbcType=VARCHAR},
				ISSHOW = #{isShow, jdbcType=VARCHAR},
				MODIFYTEXT = #{modifyText, jdbcType=CLOB},
				MODIFYREASON = #{modifyReason, jdbcType=CLOB}
        WHERE 	LAWID = #{lawId}
